CREATE TABLE IF NOT EXISTS cool.task (id BIGSERIAL PRIMARY KEY, "name" TEXT NOT NULL, note TEXT NULL, is_completed BOOLEAN NOT NULL, completed_at TIMESTAMP WITH TIME ZONE NULL, is_important BOOLEAN NOT NULL, important_at TIMESTAMP WITH TIME ZONE NULL, created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP NOT NULL, created_by_id BIGINT NULL);
CREATE TABLE IF NOT EXISTS cool.task_list (id BIGSERIAL PRIMARY KEY, "name" TEXT NOT NULL, created_by_id BIGINT NOT NULL);
CREATE TABLE IF NOT EXISTS cool."user" (id BIGSERIAL PRIMARY KEY, "name" TEXT NOT NULL, username TEXT NOT NULL, "password" TEXT NOT NULL);
